import { Component } from '@angular/core';

@Component({
  selector: 'app-product-backlog',
  templateUrl: './product-backlog.component.html',
  styleUrls: ['./product-backlog.component.css']
})
export class ProductBacklogComponent {
  newEpicName: string;
  newUserStory: string;
  userStories: string[] = [];
  developers: string[] = ['Developer 1', 'Developer 2', 'Developer 3'];
  selectedDeveloper: string;

  saveProductBacklog() {
    // Perform validation and save the epic, user stories, and selected developer to the backend or perform necessary actions
  }

  addUserStory() {
    if (this.newUserStory.trim() !== '') {
      this.userStories.push(this.newUserStory);
      this.newUserStory = '';
    }
  }
}
