import { Component, OnInit } from '@angular/core';
import { Epic } from '../epic';
import { EpicService } from '../epic.service';


@Component({
  selector: 'app-create-epic',
  templateUrl: './create-epic.component.html',
  styleUrls: ['./create-epic.component.css']
})


export class CreateEpicComponent implements OnInit{

  epic: Epic = new Epic();
  constructor(private epicService: EpicService){}

  ngOnInit(): void {

  }

  saveEpic(){
    this.epicService.createEpic(this.epic).subscribe(data =>{
      console.log(data);
    },
    error => console.log(error));
  } 

  goToEpicList(){

  }

  onSubmit(){
    console.log(this.epic);
  }
}
<h4>
    Add epic details below
</h4>

<div class="col-md-6 offset-md-3">
    <form (ngSubmit) = "onSubmit()">

        <div class="form-group">
            <label>Epic Id</label><br>
            <input type="text" class="form-control" id="epicId"
                [(ngModel)] = "epic.epicId" name = "epicId">               
        </div>

        <div class="form-group">
            <label>Project Code</label>  <br>
            <input type="text" class="form-control" id="projectCode"
                [(ngModel)] = "epic.projectCode" name = "projectCode">
        </div>

        <div class="form-group">
            <label>Name</label><br>
            <input type="text" class="form-control" id="name"
                [(ngModel)] = "epic.name" name = "name">
        </div>

        <div class="form-group">
            <label>Sprint Id</label><br>
            <input type="text" class="form-control" id="sprintId"
                [(ngModel)] = "epic.sprintId" name = "sprintId">
        </div>

        <div class="form-group">
            <label>Created On</label><br>
            <input type="text" class="form-control" id="createdOn"
                [(ngModel)] = "epic.createdOn" name = "createdOn">
        </div>

        <div class="form-group">
            <label>Completed On</label><br>
            <input type="text" class="form-control" id="completedOn"
                [(ngModel)] = "epic.completedOn" name = "completedOn">
        </div>

        <div class="form-group">
            <label>Status</label> <br>
            <input type="text" class="form-control" id="status"
                [(ngModel)] = "epic.status" name = "status">
        </div>

        <br><button class="btn btn-success" type="submit">Submit</button>
    </form>

</div>
