import { Component } from '@angular/core';

@Component({
  selector: 'app-product-backlog',
  templateUrl: './product-backlog.component.html',
  styleUrls: ['./product-backlog.component.css']
})
export class ProductBacklogComponent {
  newEpicName: string;
  newUserStory: string;
  userStories: string[] = [];

  saveProductBacklog() {
    // Perform validation and save the epic and user stories to the backend or perform necessary actions
  }

  addUserStory() {
    if (this.newUserStory.trim() !== '') {
      this.userStories.push(this.newUserStory);
      this.newUserStory = '';
    }
  }
}
