import { Component } from '@angular/core';
import { ReportService } from '../report.service';

@Component({
  selector: 'app-viewreport',
  templateUrl: './viewreport.component.html',
  styleUrls: ['./viewreport.component.css']
})
export class ViewreportComponent {
  report: Map<string, number>;

  ID: any;

  constructor(private reportService: ReportService) { }


  data: { [key: string]: number } = { New: 0 , Done: 0, Coding: 0, Planning: 0, Testing: 0 };


  getReportById(id: any): void {
    this.reportService.getReportById(id)
      .subscribe(
        (response)=>{
          console.log(response);
          this.data = response;
        }
        ,
        (error) => {
          console.log(error);
        }
      );
  }

  getObjectKeys(obj: { [key: string]: number }): string[] {
    return Object.keys(obj);
  }

}

<div class="form-group">
    <label>Enter the Project-Code:</label> <br>
    <input type="text" class="form-control" id="projectCode"
        [(ngModel)] = "ID" name = "projectCode"><br>
</div>
<button (click)="getReportById(ID)">Get Report</button>

<h2 class="desc"> Product Backlog Report </h2>
<table class="table table-striped" style="text-align: center; padding: 8px; border: 2px solid black; border-bottom: 1px solid #ccc;">
<thead>
    <tr>
        <th>Stage</th>
        <th>Count</th>

    </tr>
</thead>
<tbody>
    <tr *ngFor="let item of getObjectKeys(data)">
        <td> {{ item }}</td>
        <td>{{ data[item] }}</td>

    </tr>
</tbody>
</table>
